import './index.scss'
import { gsap } from "gsap";

const Product = () => {

    const scales = [{scale: 1.6}, {scale: 0.2}, {scale: 1}];
    gsap.to('#box-1', {
        motionPath: {
            path: scales,
            curviness: 0
        },
        duration: 3,
        ease: "none",
        repeat: -1,
        repeatDelay: 1
    });

    gsap.to('#box-2', {
        motionPath: {
            path: scales,
            curviness: 2
        },
        duration: 3,
        ease: "none",
        repeat: -1,
        repeatDelay: 1
    });


    return (
            <div className='container product-page'>
                <div className='text-product-zone'>
                    <h1>
                        Products
                    </h1>
                {/*    <Link to="/web-product" className="flat-button">*/}
                {/*        DEVELOPMENT*/}
                {/*    </Link>*/}
                {/*<br/>*/}
                {/*    <div>*/}
                {/*        <Link to="/web-product" className="web-app-button">*/}
                {/*            WEB Application*/}
                {/*        </Link>*/}
                {/*    </div>*/}
                    <main>
                        <div id="box-1">curviness: 0</div>
                        <div id="box-2">curviness: 2</div>
                    </main>
                    <a href="https://greensock.com" target="_blank">
                        <img className="gsap-3-logo" src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/16327/gsap-3-logo.svg" width="150"/></a>

                </div>

            </div>
        )
}

export default Product
